import firebase from 'firebase';

const config = {
    apiKey: "AIzaSyBOJz0OMRSF8IQJziXQQLgjMUyAuYd1Lfg",
    authDomain: "minhars-f2278.firebaseapp.com",
    databaseURL: "https://minhars-f2278.firebaseio.com",
    projectId: "minhars-f2278",
    storageBucket: "",
    messagingSenderId: "746216725713"
};
firebase.initializeApp(config);

export class MeuFirebase {

    constructor () {

        this.user = null;

        this.implementacaoFB = firebase;
        this.database = this.implementacaoFB.database();
        this.autenticacao = this.implementacaoFB.auth();

        firebase.auth().onAuthStateChanged(function(user) {
            if (user) {
                document.getElementById('autenticacao').className = 'hidden';
                document.getElementById('homeDaRS').className = '';
                document.getElementById('divNavAuth').className = '';
                this.user = user;
                document.getElementById('emailDoUsuario').innerText += " " + this.user.email;
            } else {
                document.getElementById('autenticacao').className = '';
                document.getElementById('homeDaRS').className = 'hidden';
                document.getElementById('divBotaoSair').className = 'hidden';
                this.user = null;document.getElementById('emailDoUsuario').innerText = "Seja bem-vindo ";
            }
        });

    }

    criarUsuario ( nome, email, senha, senha2 ) {
        if( senha !== senha2 ) {
            alert( "As senhas digitas não coincidem!" )
        }
        else {
            this.autenticacao.createUserWithEmailAndPassword(email, senha);
            if(this.entrar(email, senha)) {
                let user = this.autenticacao.currentUser;
                if(user) {
                    user.updateProfile({
                        displayName : nome
                    });
                }
            }
            this.sair();
            alert("Conta criada com sucesso!");
        }

    }

    entrar ( email, senha ) {
        this.autenticacao.signInWithEmailAndPassword(email, senha).then(function(error) {
            return false;
        });
        return true;
    }

    sair () {
        firebase.auth().signOut().then(function() {
            document.getElementById('autenticacao').className = "";
            alert(" Até mais! ");
        }).catch(function(error) {
            document.getElementById('autenticacao').className = "hidden";
            alert(" Houve um erro ao tentar sair. Tente novamente! ");
        });
    }

}
