import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './../css/App.css';
import { MeuFirebase } from './../js/meuFirebase';
import { FragmentoEntrar } from './../fragmentos/frag_login';
import { FragmentoCadastrar } from './../fragmentos/frag_cad';
import { FragmentoTL } from './../fragmentos/frag_timeline';

class App extends Component {

  constructor () {
    super();

    this.meuFirebase = new MeuFirebase();

    this.rsTitle = "RedeSocial";
    this.abasNavAut = [
      "Entrar",
      "Cadastrar"
    ];
  }

  iniciar () {
    setTimeout(() => {
      this.gerarFragmento(0);
      this.carregarNavAuth();
    }, 1000);
  }

  carregarNavAuth () {
    this.ulMenuAut = document.getElementById('ulMenuAut')

    for(let i = 0; i < this.abasNavAut.length; i++) {
      let navAutItem = document.createElement('li');
      navAutItem.className = "navAutItem";
      navAutItem.innerText = this.abasNavAut[i];
      if(this.abasNavAut[i] === "Entrar") {
        navAutItem.addEventListener('click', this.eventoEntrar);
      }
      else if(this.abasNavAut[i] === "Cadastrar") {
        navAutItem.addEventListener('click', this.eventoCadastrar);
      }

      this.ulMenuAut.appendChild(navAutItem);
    }
  }

  eventoEntrar = event => {
    this.gerarFragmento(0);
  }

  eventoCadastrar = event => {
    this.gerarFragmento(1);
  }

  entrar (email, senha) {
    if(this.meuFirebase.entrar(email, senha)) {
      alert("Entrou!");
    }
    else {
      alert("Ocorreu um erro ao entrar, tente novamente!");
    }
  }

  cadastrar (nome, email, senha, senha2) {
    this.meuFirebase.criarUsuario(nome, email, senha, senha2)
  }

  gerarFragmento(tipo) {
    if(tipo === 0) {
      ReactDOM.render(<FragmentoEntrar onClick={ () => { this.entrar(document.getElementById('emailEntrar').value, document.getElementById('senhaEntrar').value); } } />, document.getElementById('controladorDeFragmentos'));
    }
    else if(tipo === 1) {
      ReactDOM.render(<FragmentoCadastrar onClick={ () => { this.cadastrar(document.getElementById('nomeCad').value, document.getElementById('emailCad').value, document.getElementById('senhaCad').value, document.getElementById('senhaCad2').value); } } />, document.getElementById('controladorDeFragmentos'));
    }
    else if(tipo === 2) {
      ReactDOM.render();
    }
  }

  render() {
    return (
      <div className="App">
        <header id="header">
          <h1 id="rsTitle">{ this.rsTitle }</h1>
          <nav id="navMenuAut">
            <ul id="ulMenuAut"></ul>
          </nav>
        </header>
        <div id="meio">
          <div id="controladorDeFragmentos"></div>
        </div>
        <footer id="rodape">
          <p>&copy;</p>
        </footer>
        { this.iniciar() }
      </div>
    );
  }
}

export default App;
